int mbs_select_record(DB *db, struct mbs_record *rec, char *clause) {
	SQLRETURN ret;

	if ((ret = mbs_select(db, clause)) != 0) return ret;
	if ((ret = mbs_fetch_record(db, rec)) != 0) return ret;
	SQLCloseCursor(db->hstmt);
	return 0;
}

